	(619,'Perizinan',24,'Surat Ijin Usaha Perdagangan ( SIUP ) Besar [Modal > 10 Miliar]','24.1PB.7','{no_izin}/{kode_izin}/{kode_wilayah}/{kode_arsip}/{tahun}','Y',2,'N','N','Y',3,'Hari','N',0,'Hari','','','','','','',0,NULL,1,'SIUP','<p style=\"text-align: center;\"><strong>PEMERINTAH PROVINSI DAERAH KHUSUS IBUKOTA JAKARTA</strong></p>\n<p style=\"text-align: center;\"><strong>KANTOR PELAYANAN TERPADU SATU PINTU</strong></p>\n<p style=\"text-align: center;\"><strong>{namawil}</strong></p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\">SURAT IZIN USAHA PERDAGANGAN (SIUP) BESAR</p>\n<p style=\"text-align: center;\">Nomor : {no_sk}</p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<table>\n<tbody>\n<tr>\n<td width=\"38\">\n<p>1.</p>\n</td>\n<td width=\"312\">\n<p>Nama Perusahaan</p>\n</td>\n<td width=\"259\">\n<p style=\"text-align: justify;\">{nama_perusahaan}</p>\n</td>\n</tr>\n<tr>\n<td width=\"38\">\n<p>2.</p>\n</td>\n<td width=\"312\">\n<p>Nama Penanggung Jawab &amp; Jabatan</p>\n</td>\n<td width=\"259\">\n<p style=\"text-align: justify;\">{nama},{jabatan_perusahaan}</p>\n</td>\n</tr>\n<tr>\n<td width=\"38\">\n<p>3.</p>\n</td>\n<td width=\"312\">\n<p>Alamat Perusahaan</p>\n</td>\n<td width=\"259\">\n<p style=\"text-align: justify;\">{alamat}</p>\n</td>\n</tr>\n<tr>\n<td width=\"38\">\n<p>4.</p>\n</td>\n<td width=\"312\">\n<p>Nomor Telepon Perusahaan</p>\n</td>\n<td width=\"259\">\n<p style=\"text-align: justify;\">{telpon_perusahaan}</p>\n</td>\n</tr>\n<tr>\n<td width=\"38\">\n<p>5.</p>\n</td>\n<td width=\"312\">\n<p>Nilai Kekayaan Bersih Perusahaan</p>\n<p>(Tidak Termasuk Nilai Tanah dan Bangunan)</p>\n</td>\n<td width=\"259\">\n<p style=\"text-align: justify;\">{kekayaan_bersih}</p>\n</td>\n</tr>\n<tr>\n<td width=\"38\">\n<p>6.</p>\n</td>\n<td width=\"312\">\n<p>Kelembagaan</p>\n</td>\n<td width=\"259\">\n<p style=\"text-align: justify;\">{kelembagaan}</p>\n</td>\n</tr>\n<tr>\n<td width=\"38\">\n<p>7.</p>\n</td>\n<td width=\"312\">